#!/bin/sh
nohup bash -c "time BisFAST_align.py -p 1  -i 1 -I 2  -k /research/jsporter/Data/genome/GRCh38.p9/BisFAST/GRCh38.p9.fa /research/jsporter/Data/output/Human/BisFAST/Single/Nondirectional/SRR342552.500k.BisFAST.sam /research/jsporter/Data/reads/Human/Single/Nondirectional/SRR342552.500k.fastq" &> /research/jsporter/Data/output/Human/BisFAST/Single/Nondirectional/SRR342552.500k.BisFAST.out &

nohup bash -c "time BisFAST_align.py -p 2  -i 1 -I 2  -k /research/jsporter/Data/genome/GRCh38.p9/BisFAST/GRCh38.p9.fa /research/jsporter/Data/output/Human/BisFAST/Paired-End/PBAT/SRR2013793.500k.BisFAST.sam /research/jsporter/Data/reads/Human/Paired-End/PBAT/SRR2013793_1.500k.fastq /research/jsporter/Data/reads/Human/Paired-End/PBAT/SRR2013793_2.500k.fastq" &> /research/jsporter/Data/output/Human/BisFAST/Paired-End/PBAT/SRR2013793.500k.BisFAST.out &
